import { useState } from 'react';
// import TotalCalendar from '../components/TotalCalendar';

import moment from 'moment';
import 'react-calendar/dist/Calendar.css';
import '../css/totalCalendar.css';
import cssStyle from '../css/Schedule.module.css';
import Calendar from 'react-calendar';

// 아이콘
const IconLeft = (
  <svg xmlns='http://www.w3.org/2000/svg' height='16' width='16' viewBox='0 0 512 512'>
    <path d='M512 256A256 256 0 1 0 0 256a256 256 0 1 0 512 0zM271 135c9.4-9.4 24.6-9.4 33.9 0s9.4 24.6 0 33.9l-87 87 87 87c9.4 9.4 9.4 24.6 0 33.9s-24.6 9.4-33.9 0L167 273c-9.4-9.4-9.4-24.6 0-33.9L271 135z' />
  </svg>
);
const IconRight = (
  <svg xmlns='http://www.w3.org/2000/svg' height='16' width='16' viewBox='0 0 512 512'>
    <path d='M0 256a256 256 0 1 0 512 0A256 256 0 1 0 0 256zM241 377c-9.4 9.4-24.6 9.4-33.9 0s-9.4-24.6 0-33.9l87-87-87-87c-9.4-9.4-9.4-24.6 0-33.9s24.6-9.4 33.9 0L345 239c9.4 9.4 9.4 24.6 0 33.9L241 377z' />
  </svg>
);

export default function Schedule() {
  // 오늘날짜
  let currentDate = new Date();
  let todayMonth = currentDate.getMonth() + 1;

  // 달력
  const [nowDate, setNowDate] = useState(new Date());
  const handleDayClick = (nowDate) => {
    setNowDate(nowDate);
  };

  return (
    <main className='mw'>
      <section className={cssStyle.subSchedule}>
        <h2>공연/전시 전체일정</h2>
        <div className={cssStyle.calCon}>
          <button>{IconLeft}</button>
          <span> {todayMonth}월</span>
          <button>{IconRight}</button>
          <Calendar
            onChange={setNowDate}
            value={nowDate}
            onClickDay={handleDayClick}
            calendarType='gregory'
            formatDay={(locale, date) => moment(date).format('DD')}
          />
        </div>
      </section>
    </main>
  );
}
