import { useEffect, useState } from 'react';
import { useSelector } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import cssStyle from '../css/Ranking.module.css';

export default function Ranking() {
  let data = useSelector((a) => a.datalist);
  let [isActive, setIsActive] = useState(false);
  let [rankData, setRankData] = useState(data);
  let navigate = useNavigate();

  return (
    <main className='mw'>
      <section className={cssStyle.sec3}>
        <div className='title'>
          <h2>장르별 랭킹</h2>
          <nav>
            <ul className={cssStyle.navList}>
              {/* 오류: li클릭했을때 .on이 붙기 */}
              <li
                className={`cssStyle.${isActive && 'on'}`}
                onClick={() => {
                  setRankData(
                    [...data]
                      .filter((a) => a.category === '뮤지컬' || a.category === '오페라')
                      .sort((a, b) => b.count - a.count)
                  );
                  setIsActive(true);
                }}
              >
                # 뮤지컬/오페라
              </li>
              <li
                className={`cssStyle.${isActive && 'on'}`}
                onClick={() => {
                  setRankData(
                    [...data]
                      .filter((a) => a.category === '뮤지컬' || a.category === '오페라')
                      .sort((a, b) => b.count - a.count)
                  );
                  setIsActive(true);
                }}
              >
                # 뮤지컬/오페라
              </li>
              <li
                onClick={() => {
                  setRankData(
                    [...data].filter((a) => a.category === '발레').sort((a, b) => b.count - a.count)
                  );
                }}
              >
                # 발레
              </li>
              <li
                onClick={() => {
                  setRankData(
                    [...data]
                      .filter((a) => a.category === '성악' || a.category === '연주회')
                      .sort((a, b) => b.count - a.count)
                  );
                }}
              >
                # 성악/연주회
              </li>
              <li
                onClick={() => {
                  setRankData(
                    [...data]
                      .filter((a) => a.category === '전시' || a.category === '서예')
                      .sort((a, b) => b.count - a.count)
                  );
                }}
              >
                # 전시/서예
              </li>
              <li
                onClick={() => {
                  setRankData(
                    [...data].filter((a) => a.category === '연극').sort((a, b) => b.count - a.count)
                  );
                }}
              >
                # 연극
              </li>
            </ul>
            <button className='more' onClick={() => navigate('/ranking')}>
              전체보기
            </button>
          </nav>
        </div>
        <ul className={cssStyle.listCon}>
          {rankData.slice(0, 8).map((item, i) => (
            <li className={cssStyle.rankList} key={item.id}>
              <div className={cssStyle.imgCon}>
                <img src={`img/${item.image}`} alt={item.title} />
                <span>{i + 1}</span>
              </div>
              <strong>{item.title}</strong>
              <span>
                {item.startDate === item.endDate
                  ? item.startDate
                  : `${item.startDate} ~ ${item.endDate}`}
              </span>
            </li>
          ))}
        </ul>
      </section>
    </main>
  );
}
