import { useState } from 'react';

const btnStyle = {
  position: 'fixed',
  bottom: '10rem',
  right: '3rem',
  width: '100px',
  height: '100px',
  borderRadius: '100px',
  backgroundColor: 'var(--bk)',
  color: 'var(--wh)',
  fontSize: '4rem',
  fontWeight: '900',
};

export default function ScrollTop() {
  let [scrollBtn, setScrollBtn] = useState(false);

  const scrollTop = () => {
    window.scroll({
      top: 0,
      behavior: 'smooth',
    });
  };

  return (
    <>
      <button style={btnStyle}>Top</button>
    </>
  );
}
